import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

const Productos = () => {
  const [productos, setProductos] = useState([]);
  const [mensaje, setMensaje] = useState('');
  const location = useLocation();
  const idProducto = new URLSearchParams(location.search).get('id_producto');

  useEffect(() => {
    const fetchProductos = async () => {
      try {
        const response = idProducto
          ? await axios.get(`http://localhost/productos/productos/index.php?id_producto=${idProducto}`)
          : await axios.get('http://localhost/productos/productos/index.php');

        setProductos(response.data);
      } catch (error) {
        setMensaje('No se pudieron cargar los productos');
        console.error(error);
      }
    };

    fetchProductos();
  }, [idProducto]);

  return (
    <div>
      <h2>Productos</h2>
      {mensaje && <p>{mensaje}</p>}
      <ul>
        {productos.map((producto, index) => (
          <li key={index}>{producto.nombre}</li> 
        ))}
      </ul>
    </div>
  );
};

export default Productos;
