import React, { useState } from "react";

function App() {
  const [usuario, setUsuario] = useState("");
  const [clave, setClave] = useState("");
  const [loginMessage, setLoginMessage] = useState("");
  const [productos, setProductos] = useState([]);
  const [idProducto, setIdProducto] = useState("");
  const [producto, setProducto] = useState(null);

  const handleLogin = async () => {
    const response = await fetch("https://www.aplicacionweb.com/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ usuario, clave }),
    });
    const data = await response.json();
    setLoginMessage(data.message);
  };

  const handleCargarProductos = async () => {
    const response = await fetch("https://www.aplicacionweb.com/productos");
    const data = await response.json();
    setProductos(data);
  };

  const handleBuscarProducto = async () => {
    const response = await fetch(`https://www.aplicacionweb.com/productos?id_producto=${idProducto}`);
    const data = await response.json();
    setProducto(data);
  };

  return (
    <div>
      <h2>Login</h2>
      <input type="text" placeholder="Usuario" value={usuario} onChange={(e) => setUsuario(e.target.value)} />
      <input type="password" placeholder="Clave" value={clave} onChange={(e) => setClave(e.target.value)} />
      <button onClick={handleLogin}>Iniciar sesión</button>
      <p>{loginMessage}</p>

      <h2>Productos</h2>
      <button onClick={handleCargarProductos}>Cargar Productos</button>
      <ul>
        {productos.map((prod, index) => (
          <li key={index}>{prod.nombre}</li>
        ))}
      </ul>

      <h2>Buscar Producto</h2>
      <input type="text" placeholder="ID Producto" value={idProducto} onChange={(e) => setIdProducto(e.target.value)} />
      <button onClick={handleBuscarProducto}>Buscar</button>
      {producto && <p>{producto.nombre} - {producto.precio}€</p>}
    </div>
  );
}

export default App;
