import React, { useState } from 'react';
import { login, fetchProducts } from './api';

function App() {
    const [usuario, setUsername] = useState('');
   const [clave, setPassword] = useState('');
   const [productos, setProducts] = useState([]);
    const [mensaje, setMessage] = useState('');
     const [error, setError] = useState('');

    const handleLogin = async () => {
    try {
        const response = await login(usuario, clave);
        setMessage(response.usuario ? 'Bienvenido $ {response.usuario}': 'Login Completado');
        setError('');
        } catch (err) {
        	setMessage('');
        	setError('Credenciales no válidas')
        }
    };

    const handleFetchProducts = async () => {
        try {
                const productos = await fetchProducts();
        setProducts(productos);
        setError('');
        } catch (err) {
        	setProducts([]);
        	setError('No se pudieron cargar los productos')
        }
    };

    return (
        <div>
            <h1>Aplicación Web</h1>
            <h2>Login</h2>
            <input type="text" placeholder="Usuario" onChange={(e) => setUsername(e.target.value)} />
            <input type="password" placeholder="Contraseña" onChange={(e) => setPassword(e.target.value)} />
            <button onClick={handleLogin}>Iniciar Sesión</button>
            <p>{mensaje}</p>

            <h2>Productos</h2>
            <button onClick={handleFetchProducts}>Cargar Productos</button>
            <ul>
                {productos.map(product => (
                    <li key={product.id}>{product.nombre}</li>
                ))}
            </ul>
            <p>{error}</p>
        </div>
    );
}

export default App;
