import React, { useState } from "react";

function App() {
  const [loginMessage, setLoginMessage] = useState("");
  const [productos, setProductos] = useState([]);
  const [producto, setProducto] = useState(null);
  const [idProducto, setIdProducto] = useState("");

  const handleLogin = async () => {
    const response = await fetch("/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username: "admin", password: "1234" }),
    });
    const data = await response.json();
    setLoginMessage(data.status);
  };

  const handleCargarProductos = async () => {
    const response = await fetch("/productos");
    const data = await response.json();
    setProductos(data);
  };

  const handleBuscarProducto = async () => {
    const response = await fetch(`/productos?id_producto=${idProducto}`);
    const data = await response.json();
    setProducto(data);
  };

  return (
    <div>
      <h1>Aplicaci√≥n Web</h1>
      <button onClick={handleLogin}>Login</button>
      <p>{loginMessage}</p>

      <button onClick={handleCargarProductos}>Cargar Productos</button>
      <ul>
        {productos.map((p) => (
          <li key={p.id}>{p.nombre} - ${p.precio}</li>
        ))}
      </ul>

      <input
        type="text"
        placeholder="ID Producto"
        value={idProducto}
        onChange={(e) => setIdProducto(e.target.value)}
      />
      <button onClick={handleBuscarProducto}>Buscar Producto</button>
      {producto && <p>{producto.nombre} - ${producto.precio}</p>}
    </div>
  );
}

export default App;
