import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [productos, setProductos] = useState([]);
  const [usuario, setUsuario] = useState('');
  const [clave, setClave] = useState('');
  const [mensaje, setMensaje] = useState('');
  const [loading, setLoading] = useState(false);
  const [idProducto, setIdProducto] = useState('');

  // Función para manejar el login
  const handleLogin = async () => {
    setLoading(true);
    try {
      const response = await axios.post('https://www.aplicacionweb.com/login', {
        usuario: usuario,
        clave: clave,
      });

      setMensaje(response.data.mensaje);
    } catch (error) {
      setMensaje('Error de conexión');
    }
    setLoading(false);
  };

  // Función para cargar todos los productos
  const handleCargarProductos = async () => {
    setLoading(true);
    try {
      const response = await axios.get('https://www.aplicacionweb.com/productos');
      setProductos(response.data);
    } catch (error) {
      setMensaje('Error al cargar productos');
    }
    setLoading(false);
  };

  // Función para cargar un producto específico
  const handleCargarProducto = async () => {
    if (!idProducto) {
      setMensaje('Debes proporcionar un ID de producto');
      return;
    }
    setLoading(true);
    try {
      const response = await axios.get(`https://www.aplicacionweb.com/productos?id_producto=${idProducto}`);
      setProductos([response.data]); // Se coloca el producto específico en un array
    } catch (error) {
      setMensaje('Error al cargar el producto');
    }
    setLoading(false);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Bienvenido a la Aplicación Web</h1>

      {/* Formulario de login */}
      <div>
        <h2>Login</h2>
        <input
          type="text"
          placeholder="Usuario"
          value={usuario}
          onChange={(e) => setUsuario(e.target.value)}
          style={{ padding: '5px', marginRight: '10px' }}
        />
        <input
          type="password"
          placeholder="Clave"
          value={clave}
          onChange={(e) => setClave(e.target.value)}
          style={{ padding: '5px', marginRight: '10px' }}
        />
        <button onClick={handleLogin} disabled={loading}>
          {loading ? 'Iniciando...' : 'Login'}
        </button>
      </div>


      {mensaje && <p>{mensaje}</p>}

      <hr />


      <div>
        <h2>Productos</h2>
        <button onClick={handleCargarProductos} disabled={loading}>
          {loading ? 'Cargando productos...' : 'Cargar todos los productos'}
        </button>

        <input
          type="text"
          placeholder="ID del producto"
          value={idProducto}
          onChange={(e) => setIdProducto(e.target.value)}
          style={{ padding: '5px', marginRight: '10px', marginTop: '10px' }}
        />
        <button onClick={handleCargarProducto} disabled={loading}>
          {loading ? 'Cargando producto...' : 'Cargar producto específico'}
        </button>
      </div>

      <div>
        <h3>Lista de Productos:</h3>
        {productos && productos.length > 0 ? (
          <ul>
            {productos.map((producto, index) => (
              <li key={index}>{producto.nombre}</li>  
            ))}
          </ul>
        ) : (
          <p>No hay productos para mostrar.</p>
        )}
      </div>
    </div>
  );
};

export default App;
